{"query":"query findGame($gameId: ID!) {
  game(gameId: $gameId) {
    id
    questionRounds {
      id
      question {
        id
        hints
        answer
        question
      }
      bettingRounds {
        id
        foldedPlayerIds
        currentPlayerId
        lastRaisedPlayerId
        bets {
          amount
          playerId
        }
      }
      guesses {
        guess
        playerId
      }
    }
    currentQuestionRound
    players {
      id
      money
    }
    dealerId
  }
}
